import React, { useState } from 'react';
import { autoReplyAI } from './autoReply'; // Import autoReplyAI function
import { Fullname, ownernumber } from './settings'; // Adjusted import path

// Konstanta
const SYSTEM_PROMPT = {
    role: "system",
    content: `Kamu adalah ${Fullname} ðŸŒŸ, asisten cerdas dari NEROXOFFC. Versi 1.0. Tugasmu adalah memberikan jawaban yang selalu berbeda untuk setiap pengguna, meskipun pertanyaannya sama. Jangan lupa untuk menggunakan penebalan pada poin penting dan menyebut nama pengguna jika tersedia. Jaga setiap interaksi tetap segar dan unik!, dan jika bertanya soal owner kamu ${ownernumber} adalah nomornya, dan pencipta asli kamu adalah Nerox.`
};
const DEBUG = true;

const ChatRoom = () => {
    const [messages, setMessages] = useState([]);
    const [input, setInput] = useState('');

    const handleSendMessage = async () => {
        if (!input.trim()) return;

        const userMessage = input.trim();
        setMessages([...messages, { role: 'user', content: userMessage }]);
        setInput('');

        const response = await autoReplyAI(userMessage);

        setMessages([...messages, { role: 'user', content: userMessage }, { role: 'assistant', content: response }]);
    };

    return (
        <div className="chat-room">
            <div className="messages">
                {messages.map((msg, index) => (
                    <div key={index} className={`message ${msg.role}`}>
                        {msg.content}
                    </div>
                ))}
            </div>
            <div className="input-container">
                <textarea
                    value={input}
                    onChange={(e) => setInput(e.target.value)}
                    placeholder="Ketik pesan Anda di sini..."
                ></textarea>
                <button onClick={handleSendMessage}>Kirim</button>
            </div>
        </div>
    );
};

export default ChatRoom;
