import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './SearchMusic.css'; // Import CSS file for styling

const SearchMusic = ({ accessToken }) => {
  const [trackId, setTrackId] = useState('0o9zmvc5f3EFApU52PPIyW'); // ID lagu default
  const [searchQuery, setSearchQuery] = useState('');
  const [suggestions, setSuggestions] = useState([]);
  const [trackDetails, setTrackDetails] = useState(null);
  const [lyrics, setLyrics] = useState('');
  const [player, setPlayer] = useState(null);
  const [isPlaying, setIsPlaying] = useState(false);
  const [currentTime, setCurrentTime] = useState(0);

  // Load Spotify Web Playback SDK
  useEffect(() => {
    const script = document.createElement('script');
    script.src = 'https://sdk.scdn.co/spotify-player.js';
    script.async = true;
    document.body.appendChild(script);

    window.onSpotifyWebPlaybackSDKReady = () => {
      const player = new window.Spotify.Player({
        name: 'Web Playback SDK',
        getOAuthToken: cb => { cb(accessToken); },
        volume: 0.5
      });

      player.addListener('ready', ({ device_id }) => {
        console.log('Ready with Device ID:', device_id);
        localStorage.setItem('spotifyDeviceId', device_id);
      });

      player.addListener('not_ready', ({ device_id }) => {
        console.log('Device ID has gone offline:', device_id);
      });

      player.addListener('player_state_changed', (state) => {
        if (!state) return;
        setCurrentTime(state.position / 1000);
        setIsPlaying(!state.paused);
      });

      player.connect();
      setPlayer(player);
    };
  }, [accessToken]);

  // Search track on Spotify
  const searchTrack = async (query) => {
    try {
      const response = await axios.get('https://api.spotify.com/v1/search', {
        headers: { Authorization: `Bearer ${accessToken}` },
        params: { q: query, type: 'track', limit: 5 }
      });
      if (response.data.tracks.items.length > 0) {
        setSuggestions(response.data.tracks.items);
      }
    } catch (error) {
      console.error('Error searching track:', error);
    }
  };

  // Get track details and play
  const getTrackDetails = async (id) => {
    try {
      const response = await axios.get(`https://api.spotify.com/v1/tracks/${id}`, {
        headers: { Authorization: `Bearer ${accessToken}` }
      });
      setTrackDetails(response.data);
      getLyrics(response.data.name, response.data.artists[0].name);
      playTrack(id);
    } catch (error) {
      console.error('Error getting track details:', error);
    }
  };

  // Get lyrics
  const getLyrics = async (trackName, artistName) => {
    try {
      const response = await axios.get(`https://api.lyrics.ovh/v1/${artistName}/${trackName}`);
      setLyrics(response.data.lyrics.replace(/\n/g, '<br/>'));
    } catch {
      setLyrics('Lyrics not found.');
    }
  };

  // Play track
  const playTrack = async (id) => {
    const token = accessToken;
    const deviceId = localStorage.getItem('spotifyDeviceId');

    if (!token || !deviceId) {
      console.error('Token or Device ID is missing.');
      alert('Gagal memutar lagu. Silakan coba lagi.');
      return;
    }

    try {
      console.log('Playing track...');
      const response = await fetch(`https://api.spotify.com/v1/me/player/play?device_id=${deviceId}`, {
        method: 'PUT',
        body: JSON.stringify({ uris: [`spotify:track:${id}`] }),
        headers: { 'Content-Type': 'application/json', Authorization: `Bearer ${token}` }
      });

      if (!response.ok) {
        const error = await response.json();
        console.error('Error response from Spotify:', error);
        alert(`Error playing track: ${response.status} - ${error.error.message}`);
        return;
      }

      console.log('Track is playing!');
      setIsPlaying(true);
    } catch (error) {
      console.error('Error playing track:', error);
      alert('Terjadi kesalahan saat memutar lagu.');
    }
  };

  const pauseTrack = async () => {
    try {
      await fetch(`https://api.spotify.com/v1/me/player/pause`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json', Authorization: `Bearer ${accessToken}` }
      });
      setIsPlaying(false);
    } catch (error) {
      console.error('Error pausing track:', error);
    }
  };

  const seekTrack = async (seconds) => {
    const token = accessToken;
    const deviceId = localStorage.getItem('spotifyDeviceId');
    await fetch(`https://api.spotify.com/v1/me/player/seek?position_ms=${seconds * 1000}&device_id=${deviceId}`, {
      method: 'PUT',
      headers: { 'Content-Type': 'application/json', Authorization: `Bearer ${token}` }
    }).then(() => {
      setCurrentTime(seconds);
    }).catch((error) => {
      console.error('Error seeking track:', error);
    });
  };

  const handleSearch = (e) => {
    e.preventDefault();
    searchTrack(searchQuery);
  };

  const handleSuggestionClick = (id) => {
    setTrackId(id);
    setSuggestions([]);
    getTrackDetails(id);
  };

  return (
    <div className="spotify-player">
      <div className="polygon-background">
        <div className="polygon"></div>
        <div className="polygon"></div>
        <div className="polygon"></div>
        <div className="polygon"></div>
        <div className="polygon"></div>
      </div>
      <form onSubmit={handleSearch} className="search-form">
        <input
          type="text"
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
          placeholder="Cari lagu di Spotify"
          className="search-input"
        />
        <button type="submit" className="search-button">Cari</button>
      </form>
      {suggestions.length > 0 && (
        <div className="suggestions">
          <p>Did you mean:</p>
          <ul>
            {suggestions.map((track) => (
              <li key={track.id} onClick={() => handleSuggestionClick(track.id)}>
                {track.name} - {track.artists.map(artist => artist.name).join(', ')}
              </li>
            ))}
          </ul>
        </div>
      )}
      {trackDetails && (
        <div className="track-details">
          <div className="track-info">
            <img src={trackDetails.album.images[0].url} alt={trackDetails.name} className="album-art" />
            <div className="track-meta">
              <h2>{trackDetails.name}</h2>
              <p>Artist: {trackDetails.artists.map(artist => artist.name).join(', ')}</p>
              <p>Album: {trackDetails.album.name}</p>
              <p>Release Date: {trackDetails.album.release_date}</p>
              <p>Popularity: {trackDetails.popularity}</p>
            </div>
          </div>
          <div className="player-controls">
            {isPlaying ? (
              <button onClick={pauseTrack} className="control-button">Pause</button>
            ) : (
              <button onClick={() => playTrack(trackId)} className="control-button">Play</button>
            )}
            <button onClick={() => setIsPlaying(false)} className="control-button">Stop</button>
            <input
              type="range"
              value={currentTime}
              onChange={(e) => seekTrack(e.target.value)}
              min="0"
              max={trackDetails.duration_ms / 1000}
              className="seek-bar"
            />
          </div>
          <div className="lyrics">
            <h3>Lyrics</h3>
            <div dangerouslySetInnerHTML={{ __html: lyrics }}></div>
          </div>
        </div>
      )}
      <iframe
        title="Spotify Embed: Recommendation Playlist"
        src={`https://open.spotify.com/embed/playlist/7cAnOh7iI97WWaIB3ma7f9?utm_source=generator&theme=0`}
        width="100%"
        height="100%"
        style={{ minHeight: '360px' }}
        frameBorder="0"
        allow="autoplay; clipboard-write; encrypted-media; fullscreen; picture-in-picture"
        loading="lazy"
      />
    </div>
  );
};

export default SearchMusic;
