import React, { useState } from 'react';
import SpotifyPlayer from './SpotifyPlayer';

const Sidebar = ({ isVisible, setSidebarVisible, setActivePage, clientId, clientSecret }) => {
  const [activeTab, setActiveTab] = useState('profile');

  return (
    <div className={`sidebar ${isVisible ? 'visible' : ''}`}>
      <button className="toggle-sidebar" onClick={() => setSidebarVisible(!isVisible)}>
        {isVisible ? 'Sembunyikan Sidebar' : 'Tampilkan Sidebar'}
      </button>
      <div className="tabs">
        <button onClick={() => setActiveTab('profile')}>Profil</button>
        <button onClick={() => setActiveTab('spotify')}>Cari Lagu</button>
        <button onClick={() => setActiveTab('chat')}>Ruang Obrolan AI</button>
        <button onClick={() => setActiveTab('other')}>Kebutuhan Lain</button>
      </div>
      <div className="tab-content">
        {activeTab === 'profile' && (
          <div className="profile">
            <img src="https://via.placeholder.com/150" alt="Profile" />
            <h2>Nama Anda</h2>
            <p>Deskripsi singkat tentang Anda</p>
          </div>
        )}
        {activeTab === 'spotify' && (
          <SpotifyPlayer clientId={clientId} clientSecret={clientSecret} />
        )}
        {activeTab === 'chat' && (
          <div className="chat-room">
            <h3>Ruang Obrolan AI</h3>
            <textarea placeholder="Ketik pesan Anda di sini..."></textarea>
            <button>Kirim</button>
          </div>
        )}
        {activeTab === 'other' && (
          <div className="other-needs">
            <h3>Kebutuhan Lain</h3>
            <textarea placeholder="Ketik kebutuhan Anda di sini..."></textarea>
            <button>Kirim</button>
          </div>
        )}
      </div>
    </div>
  );
};

export default Sidebar;
