import React, { useState } from 'react';
import SpotifyPlayer from './SpotifyPlayer';

const Sidebar = ({ isVisible, setSidebarVisible, setActivePage, clientId, clientSecret }) => {
  const [activeTab, setActiveTab] = useState('profile');

  const handleTabClick = (tab) => {
    setActivePage(tab);
    setSidebarVisible(false);
  };

  return (
    <div className={`sidebar ${isVisible ? 'visible' : ''}`}>
      <button className="toggle-sidebar" onClick={() => setSidebarVisible(!isVisible)}>
        {isVisible ? 'Sembunyikan Sidebar' : 'Tampilkan Sidebar'}
      </button>
      <div className="tabs">
        <button onClick={() => handleTabClick('profile')}>Profil</button>
        <button onClick={() => handleTabClick('spotify')}>Cari Lagu</button>
        <button onClick={() => handleTabClick('chat')}>Ruang Obrolan AI</button>
        <button onClick={() => handleTabClick('other')}>Kebutuhan Lain</button>
      </div>
    </div>
  );
};

export default Sidebar;
