import React, { useState } from 'react';
import './App.css';  // Import file styling untuk komponen ini
import Snowfall from 'react-snowfall';  // Import Snowfall component
import Sidebar from './Sidebar';  // Import Sidebar component
import Profile from './Profile';  // Import Profile component
import SearchMusic from './SearchMusic';  // Import SearchMusic component
import ChatRoom from './ChatRoom';  // Import ChatRoom component

function App() {
  const clientId = 'e6f3c22f29a64e41a9d7183964127db2';  // Ganti dengan Client ID Spotify Anda
  const clientSecret = 'e8f6d5d48a0645aa8b29149dcfa50401';  // Ganti dengan Client Secret Spotify Anda
  const [snowfallStyle, setSnowfallStyle] = useState({});
  const [isSidebarVisible, setSidebarVisible] = useState(true);  // Set default to true
  const [activePage, setActivePage] = useState('profile');

  // Menangani event sentuhan
  const handleTouch = (e) => {
    const touch = e.touches[0];
    setSnowfallStyle({
      transform: `translate(${touch.clientX - window.innerWidth / 2}px, ${touch.clientY - window.innerHeight / 2}px)`,
    });
  };

  return (
    <div className="App" onTouchMove={handleTouch}>
      <div className="snowfall" style={snowfallStyle}>
        <Snowfall />  {/* Menambahkan komponen Snowfall */}
      </div>
      <button className="toggle-sidebar" onClick={() => setSidebarVisible(!isSidebarVisible)}>
        {isSidebarVisible ? 'Sembunyikan Sidebar' : 'Tampilkan Sidebar'}
      </button>
      <Sidebar
        isVisible={isSidebarVisible}
        setSidebarVisible={setSidebarVisible}
        setActivePage={setActivePage}
        clientId={clientId}
        clientSecret={clientSecret}
      />
      <div className="content">
        {activePage === 'profile' && <Profile />}
        {activePage === 'spotify' && <SearchMusic clientId={clientId} clientSecret={clientSecret} />}
        {activePage === 'chat' && <ChatRoom />}
      </div>
    </div>
  );
}

export default App;
