import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './SearchMusic.css'; // Import CSS file for styling

const SearchMusic = ({ clientId, clientSecret }) => {
  const [trackId, setTrackId] = useState('0o9zmvc5f3EFApU52PPIyW'); // ID lagu default
  const [searchQuery, setSearchQuery] = useState('');
  const [suggestions, setSuggestions] = useState([]);
  const [trackDetails, setTrackDetails] = useState(null);
  const [lyrics, setLyrics] = useState('');
  const [player, setPlayer] = useState(null);
  const [isPlaying, setIsPlaying] = useState(false);

  useEffect(() => {
    const script = document.createElement('script');
    script.src = 'https://sdk.scdn.co/spotify-player.js';
    script.async = true;
    document.body.appendChild(script);

    window.onSpotifyWebPlaybackSDKReady = () => {
      const token = localStorage.getItem('spotifyAccessToken');
      const player = new window.Spotify.Player({
        name: 'Web Playback SDK',
        getOAuthToken: cb => { cb(token); },
        volume: 0.5
      });

      player.addListener('ready', ({ device_id }) => {
        console.log('Ready with Device ID', device_id);
        localStorage.setItem('spotifyDeviceId', device_id);
      });

      player.addListener('not_ready', ({ device_id }) => {
        console.log('Device ID has gone offline', device_id);
      });

      player.connect();
      setPlayer(player);
    };
  }, []);

  const getAccessToken = async () => {
    const response = await axios.post('https://accounts.spotify.com/api/token', null, {
      headers: {
        'Content-Type': 'application/x-www-form-urlencoded',
        'Authorization': 'Basic ' + btoa(`${clientId}:${clientSecret}`)
      },
      params: {
        grant_type: 'client_credentials'
      }
    });
    const token = response.data.access_token;
    localStorage.setItem('spotifyAccessToken', token);
    return token;
  };

  const searchTrack = async (query) => {
    const token = await getAccessToken();
    const response = await axios.get('https://api.spotify.com/v1/search', {
      headers: {
        'Authorization': `Bearer ${token}`
      },
      params: {
        q: query,
        type: 'track',
        limit: 5
      }
    });
    if (response.data.tracks.items.length > 0) {
      setSuggestions(response.data.tracks.items);
    }
  };

  const getTrackDetails = async (id) => {
    const token = await getAccessToken();
    const response = await axios.get(`https://api.spotify.com/v1/tracks/${id}`, {
      headers: {
        'Authorization': `Bearer ${token}`
      }
    });
    setTrackDetails(response.data);
    getLyrics(response.data.name, response.data.artists[0].name);
    playTrack(id);
  };

  const getLyrics = async (trackName, artistName) => {
    try {
      const response = await axios.get(`https://api.lyrics.ovh/v1/${artistName}/${trackName}`);
      setLyrics(response.data.lyrics.replace(/\n/g, '<br/>'));
    } catch (error) {
      setLyrics('Lyrics not found.');
    }
  };

  const playTrack = (id) => {
    const token = localStorage.getItem('spotifyAccessToken');
    const deviceId = localStorage.getItem('spotifyDeviceId');
    fetch(`https://api.spotify.com/v1/me/player/play?device_id=${deviceId}`, {
      method: 'PUT',
      body: JSON.stringify({ uris: [`spotify:track:${id}`] }),
      headers: {
        'Content-Type': 'application/json',
        'Authorization': `Bearer ${token}`
      }
    });
    setIsPlaying(true);
  };

  const pauseTrack = () => {
    const token = localStorage.getItem('spotifyAccessToken');
    fetch(`https://api.spotify.com/v1/me/player/pause`, {
      method: 'PUT',
      headers: {
        'Content-Type': 'application/json',
        'Authorization': `Bearer ${token}`
      }
    });
    setIsPlaying(false);
  };

  const handleSearch = (e) => {
    e.preventDefault();
    searchTrack(searchQuery);
  };

  const handleSuggestionClick = (id) => {
    setTrackId(id);
    setSuggestions([]);
    getTrackDetails(id);
  };

  return (
    <div className="spotify-player">
      <form onSubmit={handleSearch} className="search-form">
        <input
          type="text"
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
          placeholder="Cari lagu di Spotify"
          className="search-input"
        />
        <button type="submit" className="search-button">Cari</button>
      </form>
      {suggestions.length > 0 && (
        <div className="suggestions">
          <p>Did you mean:</p>
          <ul>
            {suggestions.map((track) => (
              <li key={track.id} onClick={() => handleSuggestionClick(track.id)}>
                {track.name} - {track.artists.map(artist => artist.name).join(', ')}
              </li>
            ))}
          </ul>
        </div>
      )}
      {trackDetails && (
        <div className="track-details">
          <h2>{trackDetails.name}</h2>
          <p>Artist: {trackDetails.artists.map(artist => artist.name).join(', ')}</p>
          <p>Album: {trackDetails.album.name}</p>
          <p>Release Date: {trackDetails.album.release_date}</p>
          <p>Popularity: {trackDetails.popularity}</p>
          <div className="player-controls">
            {isPlaying ? (
              <button onClick={pauseTrack}>Pause</button>
            ) : (
              <button onClick={() => playTrack(trackId)}>Play</button>
            )}
            <button onClick={() => setIsPlaying(false)}>Stop</button>
          </div>
          <div className="lyrics">
            <h3>Lyrics</h3>
            <div dangerouslySetInnerHTML={{ __html: lyrics }}></div>
          </div>
        </div>
      )}
    </div>
  );
};

export default SearchMusic;
