import React, { useState } from 'react';
import axios from 'axios';
import { Fullname, ownernumber } from './settings'; // Adjusted import path

// Konstanta
const SYSTEM_PROMPT = {
    role: "system",
    content: `Kamu adalah ${Fullname} ðŸŒŸ, asisten cerdas dari NEROXOFFC. Versi 1.0. Tugasmu adalah memberikan jawaban yang selalu berbeda untuk setiap pengguna, meskipun pertanyaannya sama. Jangan lupa untuk menggunakan penebalan pada poin penting dan menyebut nama pengguna jika tersedia. Jaga setiap interaksi tetap segar dan unik!, dan jika bertanya soal owner kamu ${ownernumber} adalah nomornya, dan pencipta asli kamu adalah Nerox.`
};
const DEBUG = true;

// Fungsi Utilitas
function formatResponse(response) {
    if (!response) return "Maaf, saya tidak dapat memberikan jawaban saat ini.";
    return response.trim().replace(/\*\*([^\*]+)\*\*/g, "*$1*");
}

// Fungsi Utama Chat
async function chat(userMessage, sessionHistory) {
    const apiUrl = 'https://gemini-api-5k0h.onrender.com/gemini/chat';
    const params = { q: userMessage };

    let attempts = 0;
    while (true) {
        try {
            attempts++;
            if (DEBUG) console.log(`Percobaan ke-${attempts}`);

            const response = await axios.get(apiUrl, { params });
            if (response.data) {
                if (DEBUG) console.log("Respons API diterima:", response.data);
                return formatResponse(response.data.content);
            }
        } catch (error) {
            if (DEBUG) console.error(`Mohon maaf ada kesalahan ke-${attempts}:`, error.message);
        }
    }
}

const ChatRoom = () => {
    const [messages, setMessages] = useState([]);
    const [input, setInput] = useState('');

    const handleSendMessage = async () => {
        if (!input.trim()) return;

        const userMessage = input.trim();
        setMessages([...messages, { role: 'user', content: userMessage }]);
        setInput('');

        const session = { history: [SYSTEM_PROMPT, ...messages] };
        const response = await chat(userMessage, session.history);

        setMessages([...messages, { role: 'user', content: userMessage }, { role: 'assistant', content: response }]);
    };

    return (
        <div className="chat-room">
            <div className="messages">
                {messages.map((msg, index) => (
                    <div key={index} className={`message ${msg.role}`}>
                        {msg.content}
                    </div>
                ))}
            </div>
            <div className="input-container">
                <textarea
                    value={input}
                    onChange={(e) => setInput(e.target.value)}
                    placeholder="Ketik pesan Anda di sini..."
                ></textarea>
                <button onClick={handleSendMessage}>Kirim</button>
            </div>
        </div>
    );
};

export default ChatRoom;
