const axios = require('axios');

// Fungsi untuk membalas chat secara otomatis jika Auto AI aktif
const autoReplyAI = async (userMessage) => {
    try {
        const apiUrl = 'https://gemini-api-5k0h.onrender.com/gemini/chat'; // API URL untuk chat
        const params = { q: userMessage };

        // Coba panggil API
        const response = await axios.get(apiUrl, { params });

        // Periksa apakah respons ada dan valid
        return response.data?.content || 'Gagal mendapatkan respons AI.';
    } catch (error) {
        // Error handling yang lebih baik dengan log yang lebih jelas
        console.error("Error saat menghubungi API AI:", error);
        if (error.response) {
            console.error("Detail respons error:", error.response.data);
        }
        // Pastikan bot tetap memberi respons meski ada error
        return 'Terjadi kesalahan dalam menghubungi AI. Coba lagi nanti.';
    }
};

// Ekspor fungsi-fungsi ini agar dapat dipakai di file lain
module.exports = {
    autoReplyAI
};
